taskModel.script:
/* This element contains Javascript that is evaluated when this model is loaded. This is convenient place to set parameters, etc., without changing source code. Note that all Java classes and methods can be accessed from Javascript. The variable edu.wpi.always.Always.THIS contains the most recent instance of the system.
Note the 'eval' command in the Disco console window can be used similarly. */
/* These variables can be changed either here or using eval command in console. Note the values here only apply to the session instance of Disco. Tracing in Disco-based plugins can be set in the plugin model or plugin console. */
Packages.edu.wpi.always.Always.TRACE = true; Packages.edu.wpi.disco.rt.DiscoRT.TRACE = false;
/* Rules for progressing through closeness levels. These are here rather than in RelationshipManager.java in order to avoid compile dependency on plugin projects */
model = Packages.edu.wpi.always.Always.THIS.getUserModel(); if ( model.getCloseness() != Packages.edu.wpi.always.Closeness.Companion ) { if ( Packages.edu.wpi.always.about.AboutPlugin.isPerformed() || Packages.plugins.ExercisePlugin.isPerformed() || Packages.plugins.StorytellingPlugin.isPerformed() || Packages.edu.neu.always.skype.SkypePlugin.isPerformed() || ( Packages.edu.wpi.always.enroll.EnrollPlugin.isPerformed() && Packages.edu.wpi.always.calendar.CalendarPlugin.isPerformed() ) ) model.setCloseness(Packages.edu.wpi.always.Closeness.Companion) else if ( model.getCloseness() != Packages.edu.wpi.always.Closeness.Acquaintance ) { if ( Packages.edu.wpi.always.enroll.EnrollPlugin.isPerformed() || Packages.edu.wpi.always.calendar.CalendarPlugin.isPerformed() || ( Packages.edu.wpi.always.user.UserUtils.getDays(model) > 6 && model.getSessions() > 3 && ( Packages.edu.wpi.always.about.CheckersPlugin.isPerformed() || Packages.plugins.ExplainPlugin.isPerformed() || plugins.AnecdotesPlugin.isPerformed() || plugins.HealthPlugin.isPerformed() || plugins.NutritionPlugin.isPerformed() || Packages.edu.wpi.always.srummy.SrummyPlugin.isPerformed() || Packages.edu.wpi.always.ttt.TTTPlugin.isPerformed() ) ) ) model.setCloseness(Packages.edu.wpi.always.Closeness.Acquaintance); } }
